import { LocaleConfig, Plugin, PluginConfig } from '@vuepress/core';
import PhotoSwipe from 'photoswipe';
import { ConvertLocaleConfig } from '@mr-hope/vuepress-shared';
import PhotoSwipeDefaultUI from 'photoswipe/dist/photoswipe-ui-default';

interface PhotoSwipeLocaleData {
    /**
     * Close button label text
     *
     * 关闭按钮标签文字
     */
    close: string;
    /**
     * Full screen button label text
     *
     * 全屏按钮标签文字
     */
    fullscreen: string;
    /**
     * Share button label text
     *
     * 分享按钮标签文字
     */
    share: string;
    /**
     * Zoom button label text
     *
     * 缩放按钮标签文字
     */
    zoom: string;
    /**
     * Previous image button label text
     *
     * 上一张图片按钮标签文字
     */
    prev: string;
    /**
     * Next image button label text
     *
     * 下一张图片按钮标签文字
     */
    next: string;
    /**
     * Share button config
     *
     * 功能按钮配置
     */
    buttons: PhotoSwipeDefaultUI.ShareButtonData[];
}
declare type PhotoSwipeLocaleConfig = ConvertLocaleConfig<PhotoSwipeLocaleData>;

interface PhotoSwipeOptions {
    /**
     * Image selector
     *
     * 图片选择器
     *
     * @default '.theme-default-content :not(a) > img'
     */
    selector?: string;
    /**
     * The delay of photo-swipe fetching page images, in ms
     *
     * If the theme you are using has a switching animation, it is recommended to configure this option to `Switch animation duration + 200`
     *
     * photo-swipe 抓取页面图片的延时，单位 ms
     *
     * 如果你使用的主题有切换动画，建议配置此选项为 `切换动画时长 + 200`
     *
     * @default 500
     */
    delay?: number;
    /**
     * Options which will pass to `photo-swipe`
     *
     * 传递给 photo-swipe 的额外选项
     */
    options?: PhotoSwipe.Options;
    /**
     * Locale config
     *
     * 国际化配置
     */
    locales?: LocaleConfig<PhotoSwipeLocaleData>;
}

declare const photoSwipePlugin: Plugin<PhotoSwipeOptions>;
declare const photoSwipe: (options: PhotoSwipeOptions | false) => PluginConfig<PhotoSwipeOptions>;

export { PhotoSwipeLocaleConfig, PhotoSwipeLocaleData, PhotoSwipeOptions, photoSwipePlugin as default, photoSwipe, photoSwipePlugin };
